package loops;

public class Test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=5;i<=50;i=i+5)
		{
			System.out.println(i);
		}
		System.out.println("=========================");
		
		for (int i=5;i>=50;i=i-5)
	{
			System.out.println(i);
		}
		System.out.println("=========================");
		int j=5;
		while(j<=50)
		{
			System.out.println(j);
			j=j+5;
		}
		System.out.println("======================");
		int k=50;
		while (k>5)
		{
		System.out.println(k);
		k=k-5;
		}
		}

}
